import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';

const VietSilkApp = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [cartCount, setCartCount] = useState(0);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  // Gọi API lấy sản phẩm theo giới tính
  const fetchProducts = async (gioiTinh) => {
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/api/products?gioiTinh=${gioiTinh}`);
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Lỗi khi lấy sản phẩm:', error);
    }
    setLoading(false);
  };

  // Gọi sản phẩm mặc định khi load lần đầu
  useEffect(() => {
    fetchProducts('Nam'); // hoặc 'Nam'
  }, []);

  const handleEmailSubmit = (e) => {
    e.preventDefault();
    if (email) {
      alert('Cảm ơn bạn đã đăng ký nhận tin!');
      setEmail('');
    }
  };

  return (
    <div className="min-h-screen bg-white">
      <Header />

      {/* Banner chính */}
      <section
        className="relative bg-cover bg-center min-h-screen flex items-center justify-center"
        style={{ backgroundImage: "url('/images/bg.jpg')" }}
      >
        <div className="text-center bg-white/70 p-6 rounded shadow-lg max-w-2xl w-full mx-4">
          <h2 className="text-4xl md:text-6xl font-bold text-gray-900 mb-4">
            VietSilk clothing
          </h2>
          <p className="text-lg text-gray font-medium mb-7">
            Discover the latest collection of Viet Silks – Full of Vietnamese identity
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <button
              onClick={() => navigate('/products?gioiTinh=Nữ')}
              className="bg-white text-black px-8 py-3 font-medium hover:bg-black hover:text-white transition-colors"
            >
              Shop Women
            </button>
            <button
              onClick={() => navigate('/products?gioiTinh=Nam')}
              className="bg-white text-black px-8 py-3 font-medium hover:bg-black hover:text-white transition-colors"
            >
              Shop Men
            </button>
          </div>
        </div>
      </section>

      {/* Sản phẩm nổi bật */}
      <section className="max-w-7xl mx-auto px-4 py-12">
        <h2 className="text-2xl font-semibold mb-6 text-center">Sản phẩm nổi bật</h2>
        {loading ? (
          <p className="text-center text-gray-500 text-lg">Đang tải sản phẩm...</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {products.slice(0, 4).map((product) => {
              // Xác định folder dựa vào giới tính (Nam/Nữ)
              const folder = product.gioiTinh === 'Nam' ? 'Men' : 'Women';

              // Lấy đường dẫn ảnh đúng
              const imageUrl = product.hinhAnh
                ? `/images/${folder}/${product.hinhAnh}`
                : '/images/default-image.jpg';

              return (
                <div
                  key={product._id}
                  className="border p-2 rounded shadow hover:shadow-lg transition-shadow duration-200 cursor-pointer"
                  onClick={() => navigate(`/product/${product._id}`)}
                >
                  <div className="aspect-square w-full overflow-hidden rounded">
                    <img
                      src={imageUrl}
                      alt={product.tenSanPham}
                      className="w-full h-full object-cover"
                      onError={(e) => (e.currentTarget.src = '/images/default-image.jpg')}
                    />
                  </div>
                  <h3 className="font-medium mt-2 text-center">{product.tenSanPham}</h3>
                  <p className="text-center text-sm text-gray-700 font-semibold">
                    {product.gia.toLocaleString('vi-VN')} VND
                  </p>
                </div>
              );
            })}
          </div>
        )}
      </section>

      {/* Đăng ký email */}
      <section className="bg-gray-50 py-16">
        <div className="max-w-7xl mx-auto px-4 text-center">
          <form onSubmit={handleEmailSubmit} className="max-w-md mx-auto flex">
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="flex-1 px-4 py-3 border border-gray-300 focus:outline-none focus:ring-2 focus:ring-black"
              required
            />
            <button
              type="submit"
              className="bg-black text-white px-6 py-3 hover:bg-gray-800 transition-colors"
            >
              Sign Up
            </button>
          </form>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default VietSilkApp;
