import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FiSearch, FiHeart, FiUser, FiShoppingCart, FiLogOut } from 'react-icons/fi';

// Modal đơn giản đăng nhập/đăng ký
const AuthModal = ({ mode, onClose, onSubmit }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = e => {
    e.preventDefault();
    onSubmit({ email, password });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded max-w-sm w-full">
        <h2 className="text-xl mb-4">{mode === 'login' ? 'Đăng nhập' : 'Đăng ký'}</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            placeholder="Email"
            className="w-full border p-2 rounded"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Mật khẩu"
            className="w-full border p-2 rounded"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />
          <div className="flex justify-between items-center">
            <button
              type="submit"
              className="bg-black text-white px-4 py-2 rounded"
            >
              {mode === 'login' ? 'Đăng nhập' : 'Đăng ký'}
            </button>
            <button type="button" onClick={onClose} className="text-gray-600 hover:text-gray-900">
              Hủy
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const Header = () => {
  const navigate = useNavigate();

  // Trạng thái modal
  const [authModalOpen, setAuthModalOpen] = useState(false);
  const [authMode, setAuthMode] = useState('login'); // hoặc 'register'

  // Trạng thái đăng nhập
  const [user, setUser] = useState(null);

  // Giỏ hàng
  const [cartCount, setCartCount] = useState(0);

  // Bật tắt menu mobile
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const navigationItems = [
    { name: 'Women', items: ['New In', 'Underwear', 'Swimwear', 'Jeans', 'Clothing'] },
    { name: 'Men', items: ['New In', 'Underwear', 'Swimwear', 'Jeans', 'Clothing'] },
    { name: 'Kids', items: ['New In', 'Babies (0-4 Years)', 'Boys (4-16 Years)', 'Girls (4-16 Years)'] }
  ];

  // Giả lập lấy giỏ hàng từ localStorage hoặc backend khi user đăng nhập
  useEffect(() => {
    if (user) {
      // Lấy cartCount từ localStorage hoặc API
      const savedCart = JSON.parse(localStorage.getItem('cart')) || [];
      setCartCount(savedCart.length);
    } else {
      setCartCount(0);
    }
  }, [user]);

  // Xử lý đăng nhập (gửi lên backend)
  const handleAuthSubmit = async ({ email, password }) => {
    try {
      if (authMode === 'login') {
        // Gọi API đăng nhập
        const res = await fetch('http://localhost:5000/api/auth/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password }),
        });
        const data = await res.json();
        if (res.ok) {
          setUser(data.user);
          setAuthModalOpen(false);
          alert('Đăng nhập thành công');
        } else {
          alert(data.message || 'Đăng nhập thất bại');
        }
      } else {
        // Gọi API đăng ký
        const res = await fetch('http://localhost:5000/api/auth/register', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password }),
        });
        const data = await res.json();
        if (res.ok) {
          alert('Đăng ký thành công! Vui lòng đăng nhập.');
          setAuthMode('login');
        } else {
          alert(data.message || 'Đăng ký thất bại');
        }
      }
    } catch (error) {
      alert('Lỗi kết nối tới server');
      console.error(error);
    }
  };

  const handleLogout = () => {
    setUser(null);
    setCartCount(0);
    localStorage.removeItem('cart');
  };

  return (
    <header className="border-b border-gray-200">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <button
            className="md:hidden p-2 flex flex-col justify-between h-6 w-6"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            aria-label="Toggle menu"
          >
            <span className="block h-0.5 bg-black rounded"></span>
            <span className="block h-0.5 bg-black rounded"></span>
            <span className="block h-0.5 bg-black rounded"></span>
          </button>

          <div className="flex-shrink-0">
            <Link to="/">
              <h1 className="text-2xl font-bold tracking-wider">VietSilk</h1>
            </Link>
          </div>

          <nav className="hidden md:flex space-x-8">
            {navigationItems.map(category => (
              <div
                key={category.name}
                className="relative"
              >
               
              </div>
            ))}
          </nav>

          <div className="flex items-center space-x-6">
            <button>
              <FiSearch className="text-gray-700 hover:text-black text-2xl" />
            </button>
            <button>
              <FiHeart className="text-gray-700 hover:text-red-500 text-2xl" />
            </button>

           
            {user ? (
              <>
                <span className="text-gray-700 font-semibold">{user.email}</span>
                <button onClick={handleLogout} title="Đăng xuất">
                  <FiLogOut className="text-gray-700 hover:text-red-600 text-2xl" />
                </button>
              </>
            ) : (
              <button
                onClick={() => {
                  setAuthMode('login');
                  setAuthModalOpen(true);
                }}
              >
                <FiUser className="text-gray-700 hover:text-black text-2xl" />
              </button>
            )}

            <button
              className="relative"
              onClick={() => navigate('/cart')}
            >
              <FiShoppingCart className="text-gray-700 hover:text-black text-2xl mt-1" />
              {cartCount > 0 && (
                <span className="absolute -top-2 -right-2 bg-black text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                  {cartCount}
                </span>
              )}
            </button>
          </div>
        </div>
      </div>

    
      {authModalOpen && (
        <AuthModal
          mode={authMode}
          onClose={() => setAuthModalOpen(false)}
          onSubmit={handleAuthSubmit}
        />
      )}
    </header>
  );
};

export default Header;
