import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { FiSearch, FiHeart, FiUser, FiShoppingCart } from 'react-icons/fi';
import { useSelector } from 'react-redux';

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const [searchTerm, setSearchTerm] = useState('');
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isRegister, setIsRegister] = useState(false);
  const [authForm, setAuthForm] = useState({ name: '', emailOrPhone: '', password: '' });
  const [currentUser, setCurrentUser] = useState(null);
  const [errorMessage, setErrorMessage] = useState(''); // Thêm state lưu lỗi

  const cartItems = useSelector(state => state.cart.items);
  const cartCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  useEffect(() => {
    const user = localStorage.getItem('user');
    if (user) setCurrentUser(JSON.parse(user));
  }, []);

  const handleSearchSubmit = useCallback((e) => {
    e.preventDefault();
    if (searchTerm.trim() !== '') {
      const params = new URLSearchParams(location.search);
      params.set('q', searchTerm.trim());
      navigate(`/products?${params.toString()}`);
      setSearchTerm('');
    }
  }, [searchTerm, location.search, navigate]);

  const handleAuthSubmit = async (e) => {
    e.preventDefault();

    const url = isRegister
      ? 'http://localhost:5000/api/auth/register'
      : 'http://localhost:5000/api/auth/login';

    const payload = isRegister
      ? {
          name: authForm.name,
          email: authForm.emailOrPhone,
          password: authForm.password
        }
      : {
          email: authForm.emailOrPhone,
          password: authForm.password
        };

    try {
      const res = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      const data = await res.json();

      if (!res.ok) {
        setErrorMessage(data.message || 'Đăng nhập / đăng ký thất bại');
        return;
      }

      // Nếu thành công, reset lỗi và đóng modal
      setErrorMessage('');
      localStorage.setItem('user', JSON.stringify(data));
      setCurrentUser(data);
      setShowAuthModal(false);
      setAuthForm({ name: '', emailOrPhone: '', password: '' });
    } catch (error) {
      console.error('Lỗi:', error);
      setErrorMessage('Lỗi kết nối server.');
    }
  };

  // Khi mở modal thì reset form và lỗi
  const openAuthModal = (register = false) => {
    setIsRegister(register);
    setShowAuthModal(true);
    setAuthForm({ name: '', emailOrPhone: '', password: '' });
    setErrorMessage('');
  };

  // phần giao diện và các phần còn lại bạn giữ nguyên, mình chỉ show modal auth phần dưới đây:

  return (
    <>
      {/* phần header, nav, ... giữ nguyên như bạn đã có */}

      {/* Phần modal Auth */}
      {showAuthModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <button
              className="float-right text-gray-500 hover:text-black"
              onClick={() => setShowAuthModal(false)}
              aria-label="Đóng"
            >
              &times;
            </button>
            <h2 className="text-xl font-bold mb-4">{isRegister ? 'Đăng ký' : 'Đăng nhập'}</h2>
            <form onSubmit={handleAuthSubmit}>
              {isRegister && (
                <>
                  <input
                    type="text"
                    placeholder="Tên"
                    className="w-full mb-3 p-2 border border-gray-300 rounded"
                    required
                    value={authForm.name}
                    onChange={(e) => {
                      setAuthForm({ ...authForm, name: e.target.value });
                      setErrorMessage('');
                    }}
                  />
                  <input
                    type="text"
                    placeholder="Email hoặc số điện thoại"
                    className="w-full mb-3 p-2 border border-gray-300 rounded"
                    required
                    value={authForm.emailOrPhone}
                    onChange={(e) => {
                      setAuthForm({ ...authForm, emailOrPhone: e.target.value });
                      setErrorMessage('');
                    }}
                  />
                </>
              )}
              {!isRegister && (
                <input
                  type="text"
                  placeholder="Email hoặc số điện thoại"
                  className="w-full mb-3 p-2 border border-gray-300 rounded"
                  required
                  value={authForm.emailOrPhone}
                  onChange={(e) => {
                    setAuthForm({ ...authForm, emailOrPhone: e.target.value });
                    setErrorMessage('');
                  }}
                />
              )}
              <input
                type="password"
                placeholder="Mật khẩu"
                className="w-full mb-3 p-2 border border-gray-300 rounded"
                required
                value={authForm.password}
                onChange={(e) => {
                  setAuthForm({ ...authForm, password: e.target.value });
                  setErrorMessage('');
                }}
              />

              {/* Hiển thị lỗi ngay trên nút Đăng ký / Đăng nhập */}
              {errorMessage && (
                <p className="text-red-600 text-sm mb-2">{errorMessage}</p>
              )}

              <button
                type="submit"
                className="w-full bg-black text-white py-2 rounded hover:bg-gray-800"
              >
                {isRegister ? 'Đăng ký' : 'Đăng nhập'}
              </button>
            </form>
            <p className="mt-4 text-center text-sm">
              {isRegister ? (
                <>
                  Bạn đã có tài khoản?{' '}
                  <button
                    onClick={() => {
                      setIsRegister(false);
                      setErrorMessage('');
                      setAuthForm({ name: '', emailOrPhone: '', password: '' });
                    }}
                    className="text-blue-600 hover:underline"
                  >
                    Đăng nhập
                  </button>
                </>
              ) : (
                <>
                  Bạn chưa có tài khoản?{' '}
                  <button
                    onClick={() => {
                      setIsRegister(true);
                      setErrorMessage('');
                      setAuthForm({ name: '', emailOrPhone: '', password: '' });
                    }}
                    className="text-blue-600 hover:underline"
                  >
                    Đăng ký
                  </button>
                </>
              )}
            </p>
          </div>
        </div>
      )}
    </>
  );
};

export default Header;
