import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeItem, updateQuantity } from '../redux/shopping_cart';

const Cart = () => {
  const dispatch = useDispatch();
  const items = useSelector(state => state.cart.items);

  const handleRemove = (id) => {
    dispatch(removeItem(id));
  };

  const handleQuantityChange = (id, qty) => {
    if (qty < 1) return;
    dispatch(updateQuantity({ id, quantity: qty }));
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Giỏ hàng của bạn</h1>
      {items.length === 0 ? (
        <p>Giỏ hàng trống</p>
      ) : (
        <table className="w-full border-collapse">
          <thead>
            <tr>
              <th className="border p-2">Ảnh</th>
              <th className="border p-2">Tên sản phẩm</th>
              <th className="border p-2">Kích thước</th>
              <th className="border p-2">Màu sắc</th>
              <th className="border p-2">Số lượng</th>
              <th className="border p-2">Giá</th>
              <th className="border p-2">Tổng</th>
              <th className="border p-2">Hành động</th>
            </tr>
          </thead>
          <tbody>
            {items.map(item => (
              <tr key={item.id}>
                <td className="border p-2">
                  <img
                    src={`/images/${item.color === 'Nữ' ? 'Women' : 'Men'}/${item.image}`}
                    alt={item.name}
                    className="w-16 h-16 object-cover rounded"
                    onError={e => (e.currentTarget.src = '/images/default-image.jpg')}
                  />
                </td>
                <td className="border p-2">{item.name}</td>
                <td className="border p-2">{item.size}</td>
                <td className="border p-2">{item.color}</td>
                <td className="border p-2">
                  <input
                    type="number"
                    value={item.quantity}
                    min={1}
                    className="w-16 border rounded text-center"
                    onChange={e => handleQuantityChange(item.id, Number(e.target.value))}
                  />
                </td>
                <td className="border p-2">{item.price.toLocaleString('vi-VN')} VND</td>
                <td className="border p-2">{(item.price * item.quantity).toLocaleString('vi-VN')} VND</td>
                <td className="border p-2">
                  <button
                    className="text-red-600 hover:underline"
                    onClick={() => handleRemove(item.id)}
                  >
                    Xóa
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Cart;
