import React, { useState } from 'react';
import axios from 'axios';

const ChatWidget = () => {
  const [messages, setMessages] = useState([
    { role: 'assistant', content: 'Xin ch√†o! T√¥i c√≥ th·ªÉ gi√∫p g√¨ cho b·∫°n h√¥m nay?' }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [isOpen, setIsOpen] = useState(false); // üëà Toggle tr·∫°ng th√°i h·ªôp chat

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const response = await axios.post('https://api.openai.com/v1/chat/completions', {
        model: 'gpt-4o', // ho·∫∑c 'gpt-4'
        messages: newMessages,
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `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`,
        }
      });

      const reply = response.data.choices[0].message.content;
      setMessages([...newMessages, { role: 'assistant', content: reply }]);
    } catch (error) {
      setMessages([...newMessages, { role: 'assistant', content: 'L·ªói khi g·ªçi OpenAI API!' }]);
      console.error(error);
    }

    setLoading(false);
  };

  return (
    <>
      {!isOpen && (
        <button
          onClick={() => setIsOpen(true)}
          className="fixed bottom-6 right-6 z-50 w-[55px] h-[50px] bg-black text-white rounded-full shadow-md flex items-center justify-center hover:opacity-80"
        >
          <img
      src="/images/ChatBot.jpg"
      alt="Chat Icon"
      className="w-[40px] h-[40px] object-contain rounded-full bg-transparent"
    />
        </button>
      )}
      {isOpen && (
        <div className="fixed bottom-6 right-6 w-80 h-[500px] bg-white shadow-xl rounded-lg border flex flex-col z-50">
          <div className="flex justify-between items-center p-3 border-b">
            <h4 className="text-sm font-semibold">Chat v·ªõi VietSilk</h4>
            <button
              onClick={() => setIsOpen(false)}
              className="text-gray-500 hover:text-black text-sm"
            >
              ‚úï
            </button>
          </div>

          <div className="flex-1 overflow-auto p-3 space-y-2">
            {messages.map((msg, idx) => (
              <div
                key={idx}
                className={`p-2 rounded-md text-sm whitespace-pre-wrap ${
                  msg.role === 'user'
                    ? 'bg-black text-white self-end text-right'
                    : 'bg-gray-200 text-black self-start'
                }`}
              >
                {msg.content}
              </div>
            ))}
          </div>

          <div className="p-3 border-t flex gap-2">
            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
              className="flex-1 border px-2 py-1 text-sm"
              placeholder="Nh·∫≠p tin nh·∫Øn..."
            />
            <button
              onClick={sendMessage}
              disabled={loading}
              className="bg-black text-white px-3 py-1 text-sm"
            >
              G·ª≠i
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default ChatWidget;
