import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FiSearch, FiHeart, FiUser, FiShoppingCart } from 'react-icons/fi';

const Header = () => {
  const navigate = useNavigate();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isRegister, setIsRegister] = useState(false);

  const toggleAuthModal = () => {
    setShowAuthModal(!showAuthModal);
    setIsRegister(false);
  };

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    alert('Đăng nhập thành công (demo)');
    setShowAuthModal(false);
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    alert('Đăng ký thành công (demo)');
    setShowAuthModal(false);
  };

  const navigationItems = [
    { name: 'Nữ', items: ['Mới ', 'Đồ lót', 'Đồ bơi', 'Jeans', 'Quần áo'] },
    { name: 'Nam', items: ['Mới ', 'Đồ lót', 'Đồ bơi', 'Jeans', 'Quần áo'] },
    { name: 'Trẻ Em', items: ['Mới ', 'Bé Trai ', 'Bé Gái '] }
  ];

  return (
    <header className="border-b border-gray-200 relative">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <button
            className="md:hidden p-2 flex flex-col justify-between h-6 w-6"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            aria-label="Toggle menu"
          >
            <span className="block h-0.5 bg-black rounded"></span>
            <span className="block h-0.5 bg-black rounded"></span>
            <span className="block h-0.5 bg-black rounded"></span>
          </button>

          <div className="flex-shrink-0">
            <Link to="/">
              <h1 className="text-2xl font-bold tracking-wider">VietSilk</h1>
            </Link>
          </div>

          <nav className="hidden md:flex space-x-8">
            {navigationItems.map(category => (
              <div
                key={category.name}
                className="relative"
                onMouseEnter={() => setActiveDropdown(category.name)}
                onMouseLeave={() => setActiveDropdown(null)}
              >
                <button
                  onClick={() => {
                    let gioiTinh = '';
                    if (category.name === 'Women') gioiTinh = 'Nữ';
                    else if (category.name === 'Men') gioiTinh = 'Nam';
                    else if (category.name === 'Kids') gioiTinh = 'Bé Nam';

                    navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`);
                  }}
                  className="flex items-center space-x-1 text-gray-900 hover:text-gray-600 py-3 px-2"
                >
                  <span className="font-medium">{category.name}</span>
                </button>

                {activeDropdown === category.name && (
                  <div className="absolute top-full left-0 w-64 pt-1 z-50">
                    <div className="bg-white shadow-lg border border-gray-200 rounded-md overflow-hidden">
                      {category.items.map(item => {
                        let gioiTinh = '';
                        if (category.name === 'Women') gioiTinh = 'Nữ';
                        else if (category.name === 'Men') gioiTinh = 'Nam';
                        else if (category.name === 'Kids') {
                          gioiTinh = item.toLowerCase().includes('girl') ? 'Bé Nữ' : 'Bé Nam';
                        }

                        return (
                          <button
                            key={item}
                            onClick={() => navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`)}
                            className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                          >
                            {item}
                          </button>
                        );
                      })}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </nav>

          <div className="flex items-center space-x-6">
            <button>
              <FiSearch className="text-gray-700 hover:text-black text-2xl" />
            </button>
            <button>
              <FiHeart className="text-gray-700 hover:text-red-500 text-2xl" />
            </button>

            <button onClick={toggleAuthModal}>
              <FiUser className="text-gray-700 hover:text-black text-2xl" />
            </button>

            <button
              className="relative"
              onClick={() => navigate('/cart')}
              aria-label="Đi đến trang giỏ hàng"
            >
              <FiShoppingCart className="text-gray-700 hover:text-black text-2xl mt-1" />
              <span className="absolute -top-2 -right-2 bg-black text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                {cartCount}
              </span>
            </button>
          </div>
        </div>
      </div>

      {mobileMenuOpen && (
        <div className="md:hidden border-t border-gray-400">
          <div className="px-2 pt-2 pb-3 space-y-1">
            {navigationItems.map(category => (
              <button
                key={category.name}
                className="block px-3 py-2 text-base font-medium text-gray-900 hover:bg-gray-50 w-full text-left"
              >
                {category.name}
              </button>
            ))}
          </div>
        </div>
      )}

      {/*  modal đăng nhập/đăng ký */}
      {showAuthModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg max-w-md w-full p-6 relative">
            <button
              onClick={() => setShowAuthModal(false)}
              className="absolute top-3 right-3 text-gray-600 hover:text-gray-900"
              aria-label="Close modal"
            >
              &#x2715;
            </button>

            {!isRegister ? (
              <>
                <h2 className="text-2xl font-semibold mb-4">Đăng nhập</h2>
                <form onSubmit={handleLoginSubmit} className="space-y-4">
                  <input
                    type="email"
                    placeholder="Email"
                    required
                    className="w-full px-4 py-2 border border-gray-300 rounded"
                  />
                  <input
                    type="password"
                    placeholder="Mật khẩu"
                    required
                    className="w-full px-4 py-2 border border-gray-300 rounded"
                  />
                  <button
                    type="submit"
                    className="w-full bg-black text-white py-2 rounded hover:bg-gray-800 transition"
                  >
                    Đăng nhập
                  </button>
                </form>
                <p className="mt-4 text-center text-sm text-gray-600">
                  Chưa có tài khoản?{' '}
                  <button
                    onClick={() => setIsRegister(true)}
                    className="text-blue-600 hover:underline"
                  >
                    Đăng ký
                  </button>
                </p>
              </>
            ) : (
              <>
                <h2 className="text-2xl font-semibold mb-4">Đăng ký</h2>
                <form onSubmit={handleRegisterSubmit} className="space-y-4">
                  <input
                    type="text"
                    placeholder="Họ và tên"
                    required
                    className="w-full px-4 py-2 border border-gray-300 rounded"
                  />
                  <input
                    type="email"
                    placeholder="Email"
                    required
                    className="w-full px-4 py-2 border border-gray-300 rounded"
                  />
                  <input
                    type="password"
                    placeholder="Mật khẩu"
                    required
                    className="w-full px-4 py-2 border border-gray-300 rounded"
                  />
                  <button
                    type="submit"
                    className="w-full bg-black text-white py-2 rounded hover:bg-gray-800 transition"
                  >
                    Đăng ký
                  </button>
                </form>
                <p className="mt-4 text-center text-sm text-gray-600">
                  Đã có tài khoản?{' '}
                  <button
                    onClick={() => setIsRegister(false)}
                    className="text-blue-600 hover:underline"
                  >
                    Đăng nhập
                  </button>
                </p>
              </>
            )}
          </div>
        </div>
      )}
    </header>
  );
};

export default Header;
