import { useSelector, useDispatch } from 'react-redux';
import { updateQuantity, removeFromCart } from './redux/shopping_cart';

const cartItems = useSelector(state => state.cart.items);
const dispatch = useDispatch();

{cartItems.map(item => (
  <div key={item.id}>
    <img src={item.image} />
    <p>{item.name}</p>
    <p>{item.price}</p>

    <input
      type="number"
      value={item.quantity}
      min="1"
      onChange={(e) =>
        dispatch(updateQuantity({ id: item.id, quantity: Number(e.target.value) }))
      }
    />

    <button onClick={() => dispatch(removeFromCart(item.id))}>Xo√°</button>
  </div>
))}
export default CartPage;
