import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeItem, updateQuantity } from './redux/shopping_cart'; // Đường dẫn theo dự án bạn

const Cart = () => {
  const items = useSelector(state => state.cart.items);
  const dispatch = useDispatch();

  const handleRemove = (id, size) => {
    dispatch(removeItem({ id, size }));
  };

  const handleQuantityChange = (id, size, e) => {
    const quantity = Number(e.target.value);
    if (quantity > 0) {
      dispatch(updateQuantity({ id, size, quantity }));
    }
  };

  if (items.length === 0) return <p>Giỏ hàng trống</p>;

  return (
    <div>
      <h2>Giỏ hàng</h2>
      {items.map(item => (
        <div key={`${item.id}-${item.size}`} style={{ borderBottom: '1px solid #ccc', marginBottom: 12, paddingBottom: 12 }}>
          <img
            src={`/images/${item.image}`}
            alt={item.name}
            style={{ width: 80, height: 80, objectFit: 'cover' }}
          />
          <div>
            <h3>{item.name}</h3>
            <p>Size: {item.size}</p>
            <p>Giá: {item.price.toLocaleString('vi-VN')} VND</p>
            <input
              type="number"
              min="1"
              value={item.quantity}
              onChange={e => handleQuantityChange(item.id, item.size, e)}
              style={{ width: 50 }}
            />
            <button onClick={() => handleRemove(item.id, item.size)} style={{ marginLeft: 10, color: 'red' }}>
              Xóa
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Cart;
