import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { FiSearch, FiHeart, FiUser, FiShoppingCart } from 'react-icons/fi';
import { useSelector } from 'react-redux';

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const cartItems = useSelector(state => state.cart.items);
  const cartCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isRegister, setIsRegister] = useState(false);

  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  const suggestionRef = useRef(null);

  // Fetch suggestions khi searchTerm thay đổi (debounce)
  useEffect(() => {
    if (searchTerm.trim().length === 0) {
      setSuggestions([]);
      setShowSuggestions(false);
      return;
    }

    const delayDebounceFn = setTimeout(() => {
      fetch(`/api/products/suggestions?q=${encodeURIComponent(searchTerm.trim())}`)
        .then(res => res.json())
        .then(data => {
          setSuggestions(data || []);
          setShowSuggestions(true);
        })
        .catch(() => {
          setSuggestions([]);
          setShowSuggestions(false);
        });
    }, 300); // delay 300ms debounce

    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  // Ẩn suggestions khi click ra ngoài
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (suggestionRef.current && !suggestionRef.current.contains(event.target)) {
        setShowSuggestions(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim() !== '') {
      const currentParams = new URLSearchParams(location.search);
      const gioiTinh = currentParams.get('gioiTinh');
      let url = `/products?q=${encodeURIComponent(searchTerm.trim())}`;
      if (gioiTinh) {
        url += `&gioiTinh=${encodeURIComponent(gioiTinh)}`;
      }
      navigate(url);
      setSearchTerm('');
      setShowSuggestions(false);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    let url = `/products?q=${encodeURIComponent(suggestion)}`;
    const currentParams = new URLSearchParams(location.search);
    const gioiTinh = currentParams.get('gioiTinh');
    if (gioiTinh) {
      url += `&gioiTinh=${encodeURIComponent(gioiTinh)}`;
    }
    navigate(url);
    setSearchTerm('');
    setShowSuggestions(false);
  };

  const navigationItems = [
    { name: 'Nữ', items: ['Mới', 'Đồ lót', 'Đồ bơi', 'Jeans', 'Quần áo'] },
    { name: 'Nam', items: ['Mới', 'Đồ lót', 'Đồ bơi', 'Jeans', 'Quần áo'] },
    { name: 'Trẻ em', items: ['Mới', 'Bé Trai', 'Bé Gái'] }
  ];

  // Phần toggle modal đăng nhập / đăng ký giữ nguyên

  return (
    <header className="border-b border-gray-200 relative">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <button className="md:hidden p-2 flex flex-col justify-between h-6 w-6" onClick={() => setMobileMenuOpen(!mobileMenuOpen)} aria-label="Toggle menu">
            <span className="block h-0.5 bg-black rounded"></span>
            <span className="block h-0.5 bg-black rounded"></span>
            <span className="block h-0.5 bg-black rounded"></span>
          </button>

          <div className="flex-shrink-0">
            <Link to="/"><h1 className="text-2xl font-bold tracking-wider">VietSilk</h1></Link>
          </div>

          <nav className="hidden md:flex space-x-8">
            {navigationItems.map(category => (
              <div
                key={category.name}
                className="relative"
                onMouseEnter={() => setActiveDropdown(category.name)}
                onMouseLeave={() => setActiveDropdown(null)}
              >
                <button
                  onClick={() => {
                    let gioiTinh = category.name;
                    if (category.name === 'Trẻ em') gioiTinh = 'Trẻ em';
                    navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`);
                  }}
                  className="flex items-center space-x-1 text-gray-900 hover:text-gray-600 py-3 px-2"
                >
                  <span className="font-medium">{category.name}</span>
                </button>

                {activeDropdown === category.name && (
                  <div className="absolute top-full left-0 w-64 pt-1 z-50">
                    <div className="bg-white shadow-lg border border-gray-200 rounded-md overflow-hidden">
                      {category.items.map(item => {
                        let gioiTinh = category.name;
                        if (category.name === 'Trẻ em') {
                          if (item.toLowerCase().includes('gái')) gioiTinh = 'Bé Nữ';
                          else if (item.toLowerCase().includes('trai')) gioiTinh = 'Bé Nam';
                        }
                        return (
                          <button
                            key={item}
                            onClick={() => navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`)}
                            className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                          >
                            {item}
                          </button>
                        );
                      })}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </nav>

          <div className="flex items-center space-x-6 relative" ref={suggestionRef}>
            <form onSubmit={handleSearchSubmit} className="relative w-64">
              <input
                type="text"
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
                placeholder="Tìm kiếm..."
                className="border border-gray-300 rounded-full py-1 pl-3 pr-10 text-sm focus:outline-none focus:ring-2 focus:ring-black w-full"
                onFocus={() => searchTerm && setShowSuggestions(true)}
                autoComplete="off"
              />
              <button
                type="submit"
                className="absolute right-0 top-0 mt-1 mr-2 text-gray-700 hover:text-black"
                aria-label="Tìm kiếm"
              >
                <FiSearch className="text-2xl" />
              </button>

              {/* Suggestions dropdown */}
              {showSuggestions && suggestions.length > 0 && (
                <ul className="absolute top-full left-0 right-0 bg-white border border-gray-300 rounded-md shadow-md max-h-60 overflow-y-auto z-50 mt-1 text-sm">
                  {suggestions.map((sugg, idx) => (
                    <li
                      key={idx}
                      onClick={() => handleSuggestionClick(sugg)}
                      className="cursor-pointer px-4 py-2 hover:bg-gray-100"
                    >
                      {sugg}
                    </li>
                  ))}
                </ul>
              )}
            </form>

            <button onClick={() => navigate('/wishlist')} aria-label="Wishlist">
              <FiHeart className="text-gray-700 hover:text-red-500 text-2xl" />
            </button>

            <button onClick={() => setShowAuthModal(true)}>
              <FiUser className="text-gray-700 hover:text-black text-2xl" />
            </button>

            <button className="relative" onClick={() => navigate('/cart')} aria-label="Giỏ hàng">
              <FiShoppingCart className="text-gray-700 hover:text-black text-2xl mt-1" />
              <span className="absolute -top-2 -right-2 bg-black text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                {cartCount}
              </span>
            </button>
          </div>
        </div>
      </div>

      {/* Phần mobile menu và auth modal giữ nguyên như trước */}
    </header>
  );
};

export default Header;
