import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { FiSearch, FiHeart, FiUser, FiShoppingCart } from 'react-icons/fi';
import { useSelector } from 'react-redux';

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [searchTerm, setSearchTerm] = useState('');
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const cartItems = useSelector((state) => state.cart.items);
  const cartCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isRegister, setIsRegister] = useState(false);
  const [authForm, setAuthForm] = useState({
    name: '',
    emailOrPhone: '',
    password: '',
  });
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const user = localStorage.getItem('user');
    if (user) setCurrentUser(JSON.parse(user));
  }, []);

  const buildSearchUrl = (query, currentParams) => {
    const params = new URLSearchParams(currentParams);
    if (query) {
      params.set('q', query);
    } else {
      params.delete('q');
    }
    return `/products?${params.toString()}`;
  };

  const handleSearchSubmit = useCallback(
    (e) => {
      e.preventDefault();
      if (searchTerm.trim() !== '') {
        const url = buildSearchUrl(searchTerm.trim(), location.search);
        navigate(url);
        setSearchTerm('');
      }
    },
    [searchTerm, location.search, navigate]
  );

  const navigationItems = [
    { name: 'N·ªØ', items: ['M·ªõi', 'ƒê·ªì l√≥t', 'ƒê·ªì b∆°i', 'Jeans', 'Qu·∫ßn √°o'] },
    { name: 'Nam', items: ['M·ªõi', 'ƒê·ªì l√≥t', 'ƒê·ªì b∆°i', 'Jeans', 'Qu·∫ßn √°o'] },
    { name: 'Tr·∫ª em', items: ['M·ªõi', 'B√© Trai', 'B√© G√°i'] },
  ];

  const handleAuthSubmit = async (e) => {
    e.preventDefault();
    const url = isRegister
      ? 'http://localhost:5000/api/auth/register'
      : 'http://localhost:5000/api/auth/login';

    const payload = isRegister
      ? {
          name: authForm.name,
          email: authForm.emailOrPhone,
          password: authForm.password,
        }
      : {
          email: authForm.emailOrPhone,
          password: authForm.password,
        };

    try {
      const res = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const data = await res.json();

      if (!res.ok) {
        alert(data.message || 'ƒêƒÉng nh·∫≠p / ƒëƒÉng k√Ω th·∫•t b·∫°i');
        return;
      }

      localStorage.setItem('user', JSON.stringify(data));
      setCurrentUser(data);
      alert(`ƒêƒÉng ${isRegister ? 'k√Ω' : 'nh·∫≠p'} th√†nh c√¥ng! Ch√†o ${data.name}`);
      setShowAuthModal(false);
      setAuthForm({ name: '', emailOrPhone: '', password: '' });
    } catch (err) {
      console.error(err);
      alert('L·ªói k·∫øt n·ªëi server.');
    }
  };

