import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { updateQuantity, removeItem } from './redux/shopping_cart';
import Header from './Header';
import Footer from './Footer';
import EmptyShoppingBag from './EmptyShoppingCart';

const CartPage = () => {
  const cartItems = useSelector(state => state.cart.items);
  const dispatch = useDispatch();

  const totalQuantity = cartItems.reduce((sum, item) => sum + item.quantity, 0);
  const totalPrice = cartItems.reduce((sum, item) => sum + item.quantity * item.price, 0);

  const handleContinueShopping = () => {
    window.location.href = '/products';
  };

  const handleCheckout = () => {
    alert('Chuyển đến trang thanh toán'); // hoặc: window.location.href = '/checkout';
  };

  return (
    <div className="pb-32"> {/* chừa khoảng cho nút thanh toán cố định */}
      <Header />

      {cartItems.length === 0 ? (
        <EmptyShoppingBag onContinue={handleContinueShopping} />
      ) : (
        <>
          {cartItems.map(item => (
            <div key={item.id + item.size} className="flex items-center border-b border-gray-300 py-4 space-x-4 mx-10">
              <img
                src={item.image}
                alt={item.name}
                className="w-20 h-20 object-cover rounded"
              />
              <div className="flex-1">
                <p className="font-semibold">{item.name}</p>
                <p className="text-gray-600">{item.price.toLocaleString('vi-VN')} VND</p>
                <input
                  type="number"
                  value={item.quantity}
                  min="1"
                  onChange={(e) => {
                    const val = Number(e.target.value);
                    if (val >= 1) {
                      dispatch(updateQuantity({ id: item.id, size: item.size, quantity: val }));
                    }
                  }}
                  className="border rounded px-2 py-1 mt-1 w-20"
                />
              </div>
              <button
                onClick={() => dispatch(removeItem({ id: item.id, size: item.size }))}
                className="text-red-600 hover:text-red-800 font-semibold"
              >
                Xoá
              </button>
            </div>
          ))}

          {/* Nút Thanh toán */}
          <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-300 p-4 shadow-md z-10">
            <div className="max-w-4xl mx-auto">
              <button
                onClick={handleCheckout}
                className="w-full bg-black text-white text-lg font-semibold py-4 rounded hover:bg-gray-800 transition"
              >
                Thanh toán ({totalQuantity} sản phẩm) - {totalPrice.toLocaleString('vi-VN')}₫
              </button>
            </div>
          </div>
        </>
      )}

      <Footer />
    </div>
  );
};

export default CartPage;
