import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { FiSearch, FiHeart, FiUser, FiShoppingCart } from 'react-icons/fi';
import { useSelector } from 'react-redux';

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const suggestionRef = useRef(null);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const cartItems = useSelector(state => state.cart.items);
  const cartCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isRegister, setIsRegister] = useState(false);

  // Helper function to build search URL
  const buildSearchUrl = (query, currentParams) => {
    const params = new URLSearchParams(currentParams);
    if (query) {
      params.set('q', query);
    } else {
      params.delete('q');
    }
    return `/products?${params.toString()}`;
  };

  const handleSearchSubmit = useCallback((e) => {
    e.preventDefault();
    if (searchTerm.trim() !== '') {
      const url = buildSearchUrl(searchTerm.trim(), location.search);
      navigate(url);
      setSearchTerm('');
      setShowSuggestions(false);
    }
  }, [searchTerm, location.search, navigate]);

  const handleSuggestionClick = useCallback((suggestion) => {
    const url = buildSearchUrl(suggestion, location.search);
    navigate(url);
    setSearchTerm('');
    setShowSuggestions(false);
  }, [location.search, navigate]);

  const navigationItems = [
    { name: 'Nữ', items: ['Mới', 'Đồ lót', 'Đồ bơi', 'Jeans', 'Quần áo'] },
    { name: 'Nam', items: ['Mới', 'Đồ lót', 'Đồ bơi', 'Jeans', 'Quần áo'] },
    { name: 'Trẻ em', items: ['Mới', 'Bé Trai', 'Bé Gái'] }
  ];

  return (
    <>
      <header className="border-b border-gray-200 relative">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            {/* Mobile menu button */}
            <button
              className="md:hidden p-2 flex flex-col justify-between h-6 w-6"
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              aria-label="Toggle menu"
            >
              <span className="block h-0.5 bg-black rounded"></span>
              <span className="block h-0.5 bg-black rounded"></span>
              <span className="block h-0.5 bg-black rounded"></span>
            </button>

            <div className="flex-shrink-0">
              <Link to="/">
                <h1 className="text-2xl font-bold tracking-wider">VietSilk</h1>
              </Link>
            </div>
            <nav className="hidden md:flex space-x-8">
              {navigationItems.map(category => (
                <div
                  key={category.name}
                  className="relative"
                  onMouseEnter={() => setActiveDropdown(category.name)}
                  onMouseLeave={() => setActiveDropdown(null)}
                >
                  <button
                    onClick={() => {
                      let gioiTinh = category.name;
                      if (category.name === 'Trẻ em') gioiTinh = 'Trẻ em';
                      navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`);
                    }}
                    className="flex items-center space-x-1 text-gray-900 hover:text-gray-600 py-3 px-2"
                  >
                    <span className="font-medium">{category.name}</span>
                  </button>

                  {activeDropdown === category.name && (
                    <div className="absolute top-full left-0 w-64 pt-1 z-50">
                      <div className="bg-white shadow-lg border border-gray-200 rounded-md overflow-hidden">
                        {category.items.map(item => {
                          let gioiTinh = category.name;
                          if (category.name === 'Trẻ em') {
                            if (item.toLowerCase().includes('gái')) gioiTinh = 'Bé Nữ';
                            else if (item.toLowerCase().includes('trai')) gioiTinh = 'Bé Nam';
                          }
                          return (
                            <button
                              key={item}
                              onClick={() => navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`)}
                              className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                            >
                              {item}
                            </button>
                          );
                        })}
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </nav>

            {/* Search + Icons */}
            <div className="flex items-center space-x-6">
              <div className="relative w-64">
                <form onSubmit={handleSearchSubmit} className="relative">
                  <input
                    type="text"                    
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="Tìm kiếm..."
                    className="border border-gray-300 rounded-full py-1 pl-3 pr-10 text-sm focus:outline-none focus:ring-2 focus:ring-black w-full"
                  />

                  <button
                    type="submit"
                    className="absolute right-0 top-0 mt-1 mr-2 text-gray-700 hover:text-black"
                    aria-label="Tìm kiếm"
                  >
                    <FiSearch className="text-2xl" />
                  </button>
                </form>
              </div>

              {/* Wishlist */}
              <button onClick={() => navigate('/wishlist')} aria-label="Wishlist">
                <FiHeart className="text-gray-700 hover:text-red-500 text-2xl" />
              </button>

              {/* Auth */}
              <button onClick={() => setShowAuthModal(true)}>
                <FiUser className="text-gray-700 hover:text-black text-2xl" />
              </button>

              {/* Cart */}
              <button className="relative" onClick={() => navigate('/cart')} aria-label="Giỏ hàng">
                <FiShoppingCart className="text-gray-700 hover:text-black text-2xl mt-1" />
                <span className="absolute -top-2 -right-2 bg-black text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                  {cartCount}
                </span>
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Mobile menu */}
      {mobileMenuOpen && (
        <div className={`md:hidden fixed inset-0 bg-white z-50 p-4 transition-transform duration-300 translate-x-0`}>
          <button
            className="mb-6 p-2 border border-black rounded-md"
            onClick={() => setMobileMenuOpen(false)}
            aria-label="Đóng menu"
          >
            Đóng
          </button>

          {navigationItems.map(category => (
            <div key={category.name} className="mb-6">
              <h3 className="font-bold mb-2">{category.name}</h3>
              <ul>
                {category.items.map(item => {
                  let gioiTinh = category.name;
                  if (category.name === 'Trẻ em') {
                    if (item.toLowerCase().includes('gái')) gioiTinh = 'Bé Nữ';
                    else if (item.toLowerCase().includes('trai')) gioiTinh = 'Bé Nam';
                  }
                  return (
                    <li key={item}>
                      <button
                        onClick={() => {
                          navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`);
                          setMobileMenuOpen(false);
                        }}
                        className="block py-1 text-gray-700 hover:text-black"
                      >
                        {item}
                      </button>
                    </li>
                  );
                })}
              </ul>
            </div>
          ))}
        </div>
      )}

      {/* Auth modal */}
      {showAuthModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <button
              className="float-right text-gray-500 hover:text-black"
              onClick={() => setShowAuthModal(false)}
              aria-label="Đóng"
            >
              &times;
            </button>
            <h2 className="text-xl font-bold mb-4">{isRegister ? 'Đăng ký' : 'Đăng nhập'}</h2>
            <form onSubmit={(e) => e.preventDefault()}>
              {isRegister && (
                <>
                  <input
                    type="text"
                    placeholder="Tên"
                    className="w-full mb-3 p-2 border border-gray-300 rounded"
                    required
                  />
                  <input
                    type="text"
                    placeholder="Email hoặc số điện thoại"
                    className="w-full mb-3 p-2 border border-gray-300 rounded"
                    required
                  />
                </>
              )}
              {!isRegister && (
                <input
                  type="text"
                  placeholder="Email hoặc số điện thoại"
                  className="w-full mb-3 p-2 border border-gray-300 rounded"
                  required
                />
              )}
              <input
                type="password"
                placeholder="Mật khẩu"
                className="w-full mb-3 p-2 border border-gray-300 rounded"
                required
              />
              <button
                type="submit"
                className="w-full bg-black text-white py-2 rounded hover:bg-gray-800"
              >
                {isRegister ? 'Đăng ký' : 'Đăng nhập'}
              </button>
            </form>
            <p className="mt-4 text-center text-sm">
              {isRegister ? (
                <>
                  Bạn đã có tài khoản?{' '}
                  <button
                    onClick={() => setIsRegister(false)}
                    className="text-blue-600 hover:underline"
                  >
                    Đăng nhập
                  </button>
                </>
              ) : (
                <>
                  Bạn chưa có tài khoản?{' '}
                  <button
                    onClick={() => setIsRegister(true)}
                    className="text-blue-600 hover:underline"
                  >
                    Đăng ký
                  </button>
                </>
              )}
            </p>
          </div>
        </div>
      )}
    </>
  );
};

export default Header;
