import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FiSearch, FiHeart, FiUser, FiShoppingCart } from 'react-icons/fi';
import { useSelector } from 'react-redux';

const Header = () => {
  const navigate = useNavigate();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const cartItems = useSelector(state => state.cart.items);
  const cartCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isRegister, setIsRegister] = useState(false);

  // *** Thêm searchTerm state ***
  const [searchTerm, setSearchTerm] = useState('');

  const toggleAuthModal = () => {
    setShowAuthModal(!showAuthModal);
    setIsRegister(false);
  };

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    alert('Đăng nhập thành công (demo)');
    setShowAuthModal(false);
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    alert('Đăng ký thành công (demo)');
    setShowAuthModal(false);
  };

  // *** Xử lý submit tìm kiếm ***
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim() !== '') {
      navigate(`/products?search=${encodeURIComponent(searchTerm.trim())}`);
      setSearchTerm('');
      // Nếu muốn đóng menu mobile khi tìm kiếm cũng có thể thêm setMobileMenuOpen(false);
    }
  };

  const navigationItems = [
    { name: 'Nữ', items: ['Mới ', 'Đồ lót', 'Đồ bơi', 'Jeans', 'Quần áo'] },
    { name: 'Nam', items: ['Mới ', 'Đồ lót', 'Đồ bơi', 'Jeans', 'Quần áo'] },
    { name: 'Trẻ Em', items: ['Mới ', 'Bé Trai', 'Bé Gái'] }
  ];

  return (
    <header className="border-b border-gray-200 relative">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <button
            className="md:hidden p-2 flex flex-col justify-between h-6 w-6"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            aria-label="Toggle menu"
          >
            <span className="block h-0.5 bg-black rounded"></span>
            <span className="block h-0.5 bg-black rounded"></span>
            <span className="block h-0.5 bg-black rounded"></span>
          </button>

          <div className="flex-shrink-0">
            <Link to="/">
              <h1 className="text-2xl font-bold tracking-wider">VietSilk</h1>
            </Link>
          </div>

          <nav className="hidden md:flex space-x-8">
            {navigationItems.map(category => (
              <div
                key={category.name}
                className="relative"
                onMouseEnter={() => setActiveDropdown(category.name)}
                onMouseLeave={() => setActiveDropdown(null)}
              >
                <button
                  onClick={() => {
                    let gioiTinh = '';
                    if (category.name === 'Nữ') gioiTinh = 'Nữ';
                    else if (category.name === 'Nam') gioiTinh = 'Nam';
                    else if (category.name === 'Trẻ Em') {
                      gioiTinh = 'Trẻ Em';
                    }

                    navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`);
                  }}
                  className="flex items-center space-x-1 text-gray-900 hover:text-gray-600 py-3 px-2"
                >
                  <span className="font-medium">{category.name}</span>
                </button>

                {activeDropdown === category.name && (
                  <div className="absolute top-full left-0 w-64 pt-1 z-50">
                    <div className="bg-white shadow-lg border border-gray-200 rounded-md overflow-hidden">
                      {category.items.map(item => {
                        let gioiTinh = '';
                        if (category.name === 'Nữ') gioiTinh = 'Nữ';
                        else if (category.name === 'Nam') gioiTinh = 'Nam';
                        else if (category.name === 'Trẻ Em') {
                          if (item.toLowerCase().includes('gái')) gioiTinh = 'Bé Nữ';
                          else if (item.toLowerCase().includes('trai')) gioiTinh = 'Bé Nam';
                          else gioiTinh = 'Trẻ Em';
                        }

                        return (
                          <button
                            key={item}
                            onClick={() => navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`)}
                            className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                          >
                            {item}
                          </button>
                        );
                      })}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </nav>

          <div className="flex items-center space-x-6">
            {/* Thay button search bằng form để bắt sự kiện submit */}
            <form onSubmit={handleSearchSubmit} className="relative">
              <input
                type="text"
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
                placeholder="Tìm kiếm..."
                className="border border-gray-300 rounded-full py-1 pl-3 pr-10 text-sm focus:outline-none focus:ring-2 focus:ring-black"
              />
              <button
                type="submit"
                className="absolute right-0 top-0 mt-1 mr-2 text-gray-700 hover:text-black"
                aria-label="Tìm kiếm"
              >
                <FiSearch className="text-2xl" />
              </button>
            </form>

            <button
              onClick={() => {
                const isLoggedIn = true;
                if (!isLoggedIn) {
                  setShowAuthModal(true);
                  return;
                }
                navigate('/wishlist');
              }}
              aria-label="Đi đến trang yêu thích"
            >
              <FiHeart className="text-gray-700 hover:text-red-500 text-2xl" />
            </button>

            <button onClick={toggleAuthModal}>
              <FiUser className="text-gray-700 hover:text-black text-2xl" />
            </button>

            <button
              className="relative"
              onClick={() => navigate('/cart')}
              aria-label="Đi đến trang giỏ hàng"
            >
              <FiShoppingCart className="text-gray-700 hover:text-black text-2xl mt-1" />
              <span className="absolute -top-2 -right-2 bg-black text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                {cartCount}
              </span>
            </button>
          </div>
        </div>
      </div>

      {/* Phần mobile menu và modal giữ nguyên */}
      {/* ... */}
    </header>
  );
};

export default Header;
