import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import Header from './Header';

const Products = () => {
  const [searchParams] = useSearchParams();
  const gioiTinh = searchParams.get('gioiTinh') || '';
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      try {
        const res = await fetch(`http://localhost:5000/api/products?gioiTinh=${gioiTinh}`);
        const data = await res.json();

        // In ra một vài sản phẩm để kiểm tra
        console.log('Sample products:', data.slice(0, 3));

        // Nếu muốn in riêng hinhAnh
        data.forEach((product, i) => {
          console.log(`product[${i}].hinhAnh:`, product.hinhAnh, typeof product.hinhAnh);
        });

        setProducts(data);
      } catch (error) {
        console.error('Fetch products error:', error);
      }
      setLoading(false);
    };

    fetchProducts();
  }, [gioiTinh]);

  // Hàm lấy ảnh đầu tiên từ product.hinhAnh
  const getFirstImage = (hinhAnh, folder) => {
    if (!hinhAnh || hinhAnh.length === 0) return '/images/default-image.jpg';

    const imagesStr = hinhAnh[0];
    if (!imagesStr) return '/images/default-image.jpg';

    const images = imagesStr.split(',').map(img => img.trim()).filter(Boolean);

    if (images.length === 0) return '/images/default-image.jpg';

    return `/images/${folder}/${images[0]}`;
  };

  const folder = gioiTinh.toLowerCase() === 'nữ' ? 'Women' : 'Men';

  return (
    <>
      <Header />
      <div className="max-w-7xl mx-auto px-8 py-12">
        <h1 className="text-4xl font-light mb-12">
          Shop {folder}
        </h1>

        {loading ? (
          <p className="text-center text-gray-500">Loading products...</p>
        ) : products.length === 0 ? (
          <p className="text-center text-gray-500">No products found.</p>
        ) : (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-8">
            {products.map((product) => (
              <div key={product._id} className="cursor-pointer group">
                <div className="overflow-hidden rounded-md border border-gray-200 group-hover:shadow-lg transition-shadow duration-300">
                  <img
                    src={getFirstImage(product.hinhAnh, folder)}
                    alt={product.tenSanPham}
                    className="w-full h-[300px] object-cover group-hover:scale-105 transition-transform duration-300"
                    onError={(e) => (e.currentTarget.src = '/images/default-image.jpg')}
                  />
                </div>

                <h3 className="mt-3 text-sm font-semibold text-gray-900">{product.tenSanPham}</h3>
                <p className="text-sm text-gray-700 mt-1">{product.gia.toLocaleString('vi-VN')} VND</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default Products;
