import React, { useState, useEffect } from 'react';
import { FaSwimmer, FaTshirt, FaStar } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
const VietSilkApp = () => {
  const navigate = useNavigate();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [email, setEmail] = useState('');
  const [cartCount, setCartCount] = useState(0);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);


  const fetchProducts = async (gioiTinh) => {
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/api/products?gioiTinh=${gioiTinh}`);
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Lỗi khi lấy sản phẩm:', error);
    }
    setLoading(false);
  };

  const navigationItems = [
    {
      name: 'Women',
      items: ['New In', 'Underwear', 'Swimwear', 'Jeans', 'Clothing']
    },
    {
      name: 'Men',
      items: ['New In', 'Underwear', 'Swimwear', 'Jeans', 'Clothing']
    },
    {
      name: 'Kids',
      items: ['New In', 'Babies (0-4 Years)', 'Boys (4-16 Years)', 'Girls (4-16 Years)']
    }
  ];

  const featuredCategories = [
    {
      title: 'Swimwear Now',
      description: 'Minimalist bikinis and board shorts inspired by your next escape. Feel the heat of summer.',
      imageSrc: '/images/Men_Hero_Swim_D_Tablet.png',
      bgColor: 'from-blue-100 to-blue-200',
      textColor: 'text-blue-400'
    },
    {
      title: 'The Baselayers',
      description: 'Defining pieces for under every thing.',
      icon: <FaTshirt size={24} />,
      bgColor: 'from-gray-100 to-gray-200',
      textColor: 'text-gray-400'
    },
    {
      title: 'New Arrivals',
      description: 'The just-in styles for the new season. Explore the new classics.',
      icon: <FaStar size={24} />,
      bgColor: 'from-green-100 to-green-200',
      textColor: 'text-green-400'
    }
  ];

  const handleEmailSubmit = (e) => {
    e.preventDefault();
    if (email) {
      alert('Cảm ơn bạn đã đăng ký nhận tin!');
      setEmail('');
    }
  };

  return (
    <div className="min-h-screen bg-white">
      <Header />


     <section
  className="relative bg-cover bg-center min-h-screen flex items-center justify-center"
  style={{ backgroundImage: "url('/images/bg2.jpg')" }}
>
  <div className="text-center bg-white/70 p-6 rounded shadow-lg max-w-2xl w-full mx-4">
    <h2 className="text-4xl md:text-6xl font-bold text-gray-900 mb-4">
      VietSilk clothing
    </h2>
    <p className="text-lg text-gray font-medium mb-7">
      Discover the latest collection of Viet Silks – Full of Vietnamese identity
    </p>
    <div className="flex flex-col sm:flex-row gap-4 justify-center">
      <button
        onClick={() => navigate('/products?gioiTinh=Nữ')}
        className="bg-white text-black px-8 py-3 font-medium hover:bg-black hover:text-white transition-colors "
      >
        Shop Women
      </button>
      <button
        onClick={() => navigate('/products?gioiTinh=Nam')}
        className="bg-white text-black px-8 py-3 font-medium hover:bg-black hover:text-white transition-colors "
      >
        Shop Men
      </button>
    </div>
  </div>
</section>


      <section className="max-w-7xl mx-auto px-4 py-8">
        {loading ? (
          <p className="text-center text-gray-500 text-lg">Loading products...</p>
        ) : products.length === 0 ? (
          <p className="text-center text-gray-500 text-lg"></p>
        ) : (
          <>
            <h2 className="text-2xl font-semibold mb-6 text-center">Products</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {products.map((product) => (
                <div key={product._id} className="border p-4 rounded shadow hover:shadow-lg transition-shadow duration-200">
                  <h3 className="font-semibold mb-2">{product.tenSanPham}</h3>
                  <p className="text-gray-600 mb-2">{product.moTa}</p>
                  <p className="font-bold">{product.gia.toLocaleString('vi-VN')} VND</p>
                </div>
              ))}
            </div>
          </>
        )}
      </section>

    

      <section className="py-16">
        <div className="max-w-7xl mx-auto px-4">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {featuredCategories.map((category) => (
              <div key={category.title} className="relative group cursor-pointer">
                <div className="aspect-square bg-gray-200 mb-4">
                  <div className={`w-full h-full bg-gradient-to-br ${category.bgColor} flex items-center justify-center`}>
                    <span className={`text-6xl ${category.textColor}`}>{category.emoji}</span>
                  </div>
                </div>
                <h3 className="text-2xl font-light mb-2">{category.title}</h3>
                <p className="text-gray-600 mb-4">{category.description}</p>
                <div className="flex space-x-4">
                  <button className="border border-black px-6 py-2 hover:bg-black hover:text-white transition-colors">
                    Shop Women
                  </button>
                  <button className="border border-black px-6 py-2 hover:bg-black hover:text-white transition-colors">
                    Shop Men
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section className="bg-gray-50 py-16">
        <div className="max-w-7xl mx-auto px-4 text-center">
          <h3 className="text-2xl font-light mb-4">Enjoy 10% Off Your Order</h3>
          <ul className="text-gray-600 mb-8 space-y-2">
            <li>15% off on your birthday</li>
            <li>Early access to sales</li>
            <li>Exclusive discounts</li>
          </ul>
          <form onSubmit={handleEmailSubmit} className="max-w-md mx-auto flex">
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="flex-1 px-4 py-3 border border-gray-300 focus:outline-none focus:ring-2 focus:ring-black"
              required
            />
            <button
              type="submit"
              className="bg-black text-white px-6 py-3 hover:bg-gray-800 transition-colors"
            >
              Sign Up
            </button>
          </form>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 py-16">
        <div className="max-w-7xl mx-auto px-4">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
            <div>
              <h4 className="font-medium text-gray-900 mb-4">Help & Support</h4>
              <ul className="space-y-2 text-sm text-gray-600">
                <li><a href="#" className="hover:text-gray-900">FAQ</a></li>
                <li><a href="#" className="hover:text-gray-900">Orders & Delivery</a></li>
                <li><a href="#" className="hover:text-gray-900">Returns & Refunds</a></li>
              </ul>
            </div>

            <div>
              <h4 className="font-medium text-gray-900 mb-4">Collections</h4>
              <ul className="space-y-2 text-sm text-gray-600">
                <li><a href="#" className="hover:text-gray-900">VietSilk Sport</a></li>
                <li><a href="#" className="hover:text-gray-900">VietSilk Kids</a></li>
                <li><a href="#" className="hover:text-gray-900">VietSilk Swimwear</a></li>
              </ul>
            </div>

            <div>
              <h4 className="font-medium text-gray-900 mb-4">About Us</h4>
              <ul className="space-y-2 text-sm text-gray-600">
                <li><a href="#" className="hover:text-gray-900">About VietSilk</a></li>
                <li><a href="#" className="hover:text-gray-900">Privacy Notice</a></li>
                <li><a href="#" className="hover:text-gray-900">Terms & Conditions</a></li>
              </ul>
            </div>

            <div>
              <h4 className="font-medium text-gray-900 mb-4">Customer Service</h4>
              <div className="space-y-3 text-sm text-gray-600">
                <div>
                  <div className="font-medium text-gray-900">Secure payments</div>
                  <div> Mastercard, VISA</div>
                </div>
              </div>
            </div>
          </div>

          <div className="border-t border-gray-200 mt-12 pt-8 text-center text-sm text-gray-600">
            © 2025 VietSilk Inc. All Rights Reserved
          </div>
        </div>
      </footer>
    </div>
  );
};

export default VietSilkApp;
