import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { addItem } from './redux/shopping_cart'; 

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(false);
  const [imgIndex, setImgIndex] = useState(0);
  const [selectedSize, setSelectedSize] = useState('S');
  
  useEffect(() => {
    const fetchProduct = async () => {
      setLoading(true);
      try {
        const res = await fetch(`http://localhost:5000/api/products/${id}`);
        const data = await res.json();
        setProduct(data);
        if (data.kichThuoc) {
          const sizes = data.kichThuoc.split(',').map(s => s.trim());
          setSelectedSize(sizes[0] || 'S');
        }
      } catch (error) {
        console.error(error);
      }
      setLoading(false);
    };
    fetchProduct();
  }, [id]);

  if (loading || !product) {
    return <p>Loading...</p>;
  }

  const images = product.hinhAnh[0]
    .split(',')
    .map(i => i.trim())
    .filter(Boolean);

  const handleAddToCart = () => {
    const item = {
      id: product._id || product.id,
      name: product.tenSanPham,
      price: product.gia,
      quantity: 1,
      size: selectedSize,
      color: product.mauSac || '',
      image: images[imgIndex], // Lấy ảnh thumbnail để hiển thị trong giỏ
    };
    dispatch(addItem(item));
    navigate('/cart');
  };

  return (
    <div>
      {/* ... phần hiển thị ảnh, tên sản phẩm, chọn size ... */}
      <div>
        {images.map((img, idx) => (
          <img
            key={idx}
            src={`/images/${product.gioiTinh === 'Nữ' ? 'Women' : 'Men'}/${img}`}
            alt={`thumb-${idx}`}
            style={{ width: 50, cursor: 'pointer', border: imgIndex === idx ? '2px solid black' : 'none' }}
            onClick={() => setImgIndex(idx)}
          />
        ))}
      </div>

      <div>
        <p>Chọn kích thước:</p>
        {product.kichThuoc &&
          product.kichThuoc.split(',').map(size => (
            <button
              key={size}
              onClick={() => setSelectedSize(size.trim())}
              style={{
                fontWeight: selectedSize === size.trim() ? 'bold' : 'normal',
                marginRight: 8,
              }}
            >
              {size.trim()}
            </button>
          ))}
      </div>

      <button onClick={handleAddToCart}>Thêm vào giỏ</button>
    </div>
  );
};

export default ProductDetail;
