import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { FiSearch, FiHeart, FiUser, FiShoppingCart } from 'react-icons/fi';
import { useSelector } from 'react-redux';
import axios from 'axios';

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const cartItems = useSelector(state => state.cart.items);
  const cartCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  const [showAuthModal, setShowAuthModal] = useState(false);
  const [isRegister, setIsRegister] = useState(false);
  const [authForm, setAuthForm] = useState({ name: '', email: '', password: '' });
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const toggleAuthModal = () => {
    setShowAuthModal(!showAuthModal);
    setIsRegister(false);
    setAuthForm({ name: '', email: '', password: '' });
    setErrorMessage('');
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim() !== '') {
      const currentParams = new URLSearchParams(location.search);
      const gioiTinh = currentParams.get('gioiTinh');
      let url = `/products?q=${encodeURIComponent(searchTerm.trim())}`;
      if (gioiTinh) {
        url += `&gioiTinh=${encodeURIComponent(gioiTinh)}`;
      }
      navigate(url);
      setSearchTerm('');
    }
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrorMessage('');

    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', {
        email: authForm.email,
        password: authForm.password,
      });

      const userData = res.data.user;
      localStorage.setItem('user', JSON.stringify(userData));
      setUser(userData);
      setShowAuthModal(false);
    } catch (err) {
      setErrorMessage(err.response?.data?.message || 'Đăng nhập thất bại');
    } finally {
      setLoading(false);
    }
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrorMessage('');

    try {
      await axios.post('http://localhost:5000/api/auth/register', {
        name: authForm.name,
        email: authForm.email,
        password: authForm.password,
      });

      setIsRegister(false);
      setAuthForm({ name: '', email: '', password: '' });
    } catch (err) {
      setErrorMessage(err.response?.data?.message || 'Đăng ký thất bại');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
  };

  const navigationItems = [
    { name: 'Nữ', items: ['Mới', 'Đồ lót', 'Đồ bơi', 'Jeans', 'Quần áo'] },
    { name: 'Nam', items: ['Mới', 'Đồ lót', 'Đồ bơi', 'Jeans', 'Quần áo'] },
    { name: 'Trẻ em', items: ['Mới', 'Bé Trai', 'Bé Gái'] }
  ];

  return (
    <header className="border-b border-gray-200 relative">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <button className="md:hidden p-2 flex flex-col justify-between h-6 w-6" onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>
            <span className="block h-0.5 bg-black rounded"></span>
            <span className="block h-0.5 bg-black rounded"></span>
            <span className="block h-0.5 bg-black rounded"></span>
          </button>

          <div className="flex-shrink-0">
            <Link to="/"><h1 className="text-2xl font-bold tracking-wider">VietSilk</h1></Link>
          </div>

          <nav className="hidden md:flex space-x-8">
            {navigationItems.map(category => (
              <div key={category.name} className="relative" onMouseEnter={() => setActiveDropdown(category.name)} onMouseLeave={() => setActiveDropdown(null)}>
                <button
                  onClick={() => {
                    let gioiTinh = category.name;
                    navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`);
                  }}
                  className="flex items-center space-x-1 text-gray-900 hover:text-gray-600 py-3 px-2"
                >
                  <span className="font-medium">{category.name}</span>
                </button>
                {activeDropdown === category.name && (
                  <div className="absolute top-full left-0 w-64 pt-1 z-50">
                    <div className="bg-white shadow-lg border border-gray-200 rounded-md overflow-hidden">
                      {category.items.map(item => {
                        let gioiTinh = category.name;
                        if (category.name === 'Trẻ em') {
                          if (item.toLowerCase().includes('gái')) gioiTinh = 'Bé Nữ';
                          else if (item.toLowerCase().includes('trai')) gioiTinh = 'Bé Nam';
                        }
                        return (
                          <button
                            key={item}
                            onClick={() => navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`)}
                            className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                          >
                            {item}
                          </button>
                        );
                      })}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </nav>

          <div className="flex items-center space-x-6">
            <form onSubmit={handleSearchSubmit} className="relative">
              <input
                type="text"
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
                placeholder="Tìm kiếm..."
                className="border border-gray-300 rounded-full py-1 pl-3 pr-10 text-sm focus:outline-none focus:ring-2 focus:ring-black"
              />
              <button type="submit" className="absolute right-0 top-0 mt-1 mr-2 text-gray-700 hover:text-black">
                <FiSearch className="text-2xl" />
              </button>
            </form>

            <button onClick={() => navigate('/wishlist')}><FiHeart className="text-2xl text-gray-700 hover:text-red-500" /></button>

            {user ? (
              <div className="relative group cursor-pointer font-medium">
                {user.name}
                <div className="absolute right-0 mt-2 bg-white border rounded shadow-md opacity-0 group-hover:opacity-100 transition z-10">
                  <button onClick={handleLogout} className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 w-full text-left">
                    Đăng xuất
                  </button>
                </div>
              </div>
            ) : (
              <button onClick={toggleAuthModal}><FiUser className="text-2xl text-gray-700 hover:text-black" /></button>
            )}

            <button className="relative" onClick={() => navigate('/cart')}>
              <FiShoppingCart className="text-2xl text-gray-700 hover:text-black mt-1" />
              <span className="absolute -top-2 -right-2 bg-black text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                {cartCount}
              </span>
            </button>
          </div>
        </div>
      </div>

      {mobileMenuOpen && (
        <div className="md:hidden border-t border-gray-300 bg-white shadow-sm">
          <div className="px-4 py-3 space-y-2">
            {['Nữ', 'Nam', 'Trẻ em'].map((gioiTinh) => (
              <button
                key={gioiTinh}
                onClick={() => {
                  navigate(`/products?gioiTinh=${encodeURIComponent(gioiTinh)}`);
                  setMobileMenuOpen(false);
                }}
                className="block w-full text-left text-base text-gray-900 hover:bg-gray-100 px-2 py-2 rounded"
              >
                {gioiTinh}
              </button>
            ))}
          </div>
        </div>
      )}

      {showAuthModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg max-w-md w-full p-6 relative">
            <button onClick={() => setShowAuthModal(false)} className="absolute top-3 right-3 text-gray-600 hover:text-gray-900" aria-label="Close modal">
              &#x2715;
            </button>
            <h2 className="text-2xl font-semibold mb-4">{isRegister ? 'Đăng ký' : 'Đăng nhập'}</h2>
            <form onSubmit={isRegister ? handleRegisterSubmit : handleLoginSubmit} className="space-y-4">
              {isRegister && (
                <input
                  type="text"
                  placeholder="Họ và tên"
                  required
                  value={authForm.name}
                  onChange={e => setAuthForm({ ...authForm, name: e.target.value })}
                  className="w-full px-4 py-2 border border-gray-300 rounded"
                />
              )}
              <input
                type="email"
                placeholder="Email"
                required
                value={authForm.email}
                onChange={e => setAuthForm({ ...authForm, email: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded"
              />
              <input
                type="password"
                placeholder="Mật khẩu"
                required
                value={authForm.password}
                onChange={e => setAuthForm({ ...authForm, password: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded"
              />
              {errorMessage && <p className="text-red-500 text-sm">{errorMessage}</p>}
              <button type="submit" disabled={loading} className="w-full bg-black text-white py-2 rounded hover:bg-gray-800 transition">
                {loading ? 'Đang xử lý...' : isRegister ? 'Đăng ký' : 'Đăng nhập'}
              </button>
            </form>
            <p className="mt-4 text-center text-sm text-gray-600">
              {isRegister ? 'Đã có tài khoản? ' : 'Chưa có tài khoản? '}
              <button onClick={() => setIsRegister(!isRegister)} className="text-blue-600 hover:underline">
                {isRegister ? 'Đăng nhập' : 'Đăng ký'}
              </button>
            </p>
          </div>
        </div>
      )}
    </header>
  );
};

export default Header;
