import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { updateQuantity, removeItem } from './redux/shopping_cart';
import Header from './Header';
import Footer from './Footer';
import EmptyShoppingBag from './EmptyShoppingCart';  // import component mới

const CartPage = () => {
  const cartItems = useSelector(state => state.cart.items);
  const dispatch = useDispatch();

  const handleContinueShopping = () => {
    window.location.href = '/products?gioiTinh=Nữ';
  };

  return (
    <div>
      <Header />

      {cartItems.length === 0 ? (
        <EmptyShoppingBag onContinue={handleContinueShopping} />
      ) : (
        cartItems.map(item => (
          <div key={item.id} className="flex items-center border-b border-gray-300 py-4 space-x-4">
            <img
              src={item.image}
              alt={item.name}
              className="w-20 h-20 object-cover rounded"
            />
            <div className="flex-1">
              <p className="font-semibold">{item.name}</p>
              <p className="text-gray-600">{item.price.toLocaleString('vi-VN')} VND</p>
              <input
                type="number"
                value={item.quantity}
                min="1"
                onChange={(e) =>
                  dispatch(updateQuantity({ id: item.id, quantity: Number(e.target.value) }))
                }
                className="border rounded px-2 py-1 mt-1 w-20"
              />
            </div>
            <button
              onClick={() => dispatch(removeItem(item.id))}
              className="text-red-600 hover:text-red-800 font-semibold"
            >
              Xoá
            </button>
          </div>
        ))
      )}

      <Footer />
    </div>
  );
};

export default CartPage;
