import React, { useEffect, useState } from 'react';
import Header from './Header';
import Footer from './Footer';

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchOrders = async () => {
      const storedUser = JSON.parse(localStorage.getItem('user'));
      const userId = storedUser?._id || storedUser?.id;

      if (!userId) {
        setError('Bạn cần đăng nhập để xem đơn hàng.');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(`http://localhost:5000/api/orders/${userId}`);
        const data = await response.json();

        if (response.ok) {
          setOrders(data);
        } else {
          setError(data.message || 'Không thể tải đơn hàng.');
        }
      } catch (err) {
        setError('Lỗi khi tải đơn hàng.');
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  return (
    <div className="min-h-screen flex flex-col">
      <Header />

      <main className="flex-grow p-6 max-w-4xl mx-auto">
        <h1 className="text-2xl font-bold mb-4">🧾 Đơn hàng của tôi</h1>

        {loading && <p>Đang tải đơn hàng...</p>}
        {error && <p className="text-red-600">{error}</p>}

        {!loading && orders.length === 0 && !error && (
          <p>Hiện chưa có đơn hàng nào.</p>
        )}

        {!loading && orders.map(order => (
          <div key={order._id} className="border p-4 mb-4 rounded shadow-sm">
            <p><strong>Mã đơn hàng:</strong> {order._id}</p>
            <p><strong>Trạng thái:</strong> {order.status}</p>
            <p><strong>Tổng tiền:</strong> {order.total.toLocaleString('vi-VN')}₫</p>
            <p><strong>Địa chỉ:</strong> {order.address}</p>
            <p><strong>Ngày đặt:</strong> {new Date(order.createdAt).toLocaleString()}</p>

            <div className="mt-3">
              <p className="font-semibold">Sản phẩm:</p>
              <ul className="ml-4 list-disc">
                {order.items.map((item, idx) => (
                  <li key={idx}>
                    {item.name} - {item.size} x {item.quantity} ({item.price.toLocaleString('vi-VN')}₫)
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </main>

      <Footer />
    </div>
  );
};

export default MyOrders;
