import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeItem, updateQuantity, clearCart } from './redux/shopping_cart';
import Header from './Header';
import Footer from './Footer';

const Cart = () => {
  const dispatch = useDispatch();
  const items = useSelector(state => state.cart.items);

  const handleRemove = (id, size) => {
    // Nếu muốn xóa item theo id + size để phân biệt size khác nhau
    dispatch(removeItem({ id, size }));
  };

  const handleQuantityChange = (id, size, newQty) => {
    if (newQty < 1) return;
    dispatch(updateQuantity({ id, size, quantity: newQty }));
  };

  return (
    <>
      <Header />
      <div className="max-w-5xl mx-auto p-8">
        <h1 className="text-3xl font-bold mb-6">Giỏ hàng</h1>
        {items.length === 0 ? (
          <p>Giỏ hàng đang trống.</p>
        ) : (
          <div>
            {items.map(item => (
              <div key={`${item.id}-${item.size}`} className="flex items-center gap-4 mb-6 border-b pb-4">
                <img
                  src={item.image}
                  alt={item.name}
                  className="w-24 h-24 object-cover rounded"
                  onError={e => (e.currentTarget.src = '/images/default-image.jpg')}
                />
                <div className="flex-grow">
                  <h2 className="text-xl font-semibold">{item.name}</h2>
                  <p>Size: {item.size}</p>
                  <p>Màu sắc: {item.color || 'Không xác định'}</p>
                  <p>Giá: {item.price.toLocaleString('vi-VN')} VND</p>
                  <div className="mt-2 flex items-center gap-2">
                    <button
                      onClick={() => handleQuantityChange(item.id, item.size, item.quantity - 1)}
                      className="border px-2 rounded"
                    >
                      -
                    </button>
                    <span>{item.quantity}</span>
                    <button
                      onClick={() => handleQuantityChange(item.id, item.size, item.quantity + 1)}
                      className="border px-2 rounded"
                    >
                      +
                    </button>
                  </div>
                </div>
                <button
                  onClick={() => handleRemove(item.id, item.size)}
                  className="text-red-600 font-semibold hover:underline"
                >
                  Xóa
                </button>
              </div>
            ))}
            <button
              onClick={() => dispatch(clearCart())}
              className="mt-6 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
            >
              Xóa tất cả
            </button>
          </div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default Cart;
