import { createSlice } from '@reduxjs/toolkit';

const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    items: [], // mảng sản phẩm {id, name, quantity, size, image, ...}
  },
  reducers: {
    addItem: (state, action) => {
     const itemToAdd = {
  id: product._id || product.id,
  name: product.tenSanPham,
  price: product.gia,
  quantity: 1,
  size: selectedSize,
  color: product.mauSac || '',
  image: images[imgIndex], 
  gender: product.gioiTinh === 'Nữ' ? 'Women' : 'Men',
};

    },
    removeItem: (state, action) => {
      // Xóa theo id và size để chính xác
      const { id, size } = action.payload;
      state.items = state.items.filter(i => !(i.id === id && i.size === size));
    },
    updateQuantity: (state, action) => {
      const { id, quantity, size } = action.payload;
      const item = state.items.find(i => i.id === id && i.size === size);
      if (item) {
        item.quantity = quantity;
      }
    },
    clearCart: (state) => {
      state.items = [];
    },
  },
});

export const { addItem, removeItem, updateQuantity, clearCart } = cartSlice.actions;
export default cartSlice.reducer;
