import React, { useEffect, useState, useMemo } from 'react';
import { useSearchParams } from 'react-router-dom';
import Header from './Header';

const Products = () => {
  const [searchParams] = useSearchParams();
  const gioiTinh = searchParams.get('gioiTinh') || '';
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [imgIndexes, setImgIndexes] = useState({});

  // Memoize folder name
  const folder = useMemo(() => {
    return gioiTinh.toLowerCase() === 'nữ' ? 'Women' : 'Men';
  }, [gioiTinh]);

  // Fetch products
  useEffect(() => {
    if (!gioiTinh) return;

    const fetchProducts = async () => {
      setLoading(true);
      try {
        const res = await fetch(`http://localhost:5000/api/products?gioiTinh=${gioiTinh}`);
        const data = await res.json();
        setProducts(data);

        const initialIndexes = {};
        data.forEach((p) => {
          initialIndexes[p._id] = 0;
        });
        setImgIndexes(initialIndexes);
      } catch (error) {
        console.error('Fetch products error:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [gioiTinh]);

  // Tách logic lấy ảnh
  const getImagesArray = (hinhAnh) => {
    if (!hinhAnh || hinhAnh.length === 0) return [];
    return hinhAnh[0].split(',').map(img => img.trim()).filter(Boolean);
  };

  // Chuyển ảnh
  const changeImageIndex = (productId, direction) => {
    setImgIndexes(prev => {
      const product = products.find(p => p._id === productId);
      if (!product) return prev;

      const images = getImagesArray(product.hinhAnh);
      if (images.length === 0) return prev;

      let newIndex = prev[productId] ?? 0;
      newIndex = direction === 'next'
        ? (newIndex + 1) % images.length
        : (newIndex - 1 + images.length) % images.length;

      return { ...prev, [productId]: newIndex };
    });
  };

  return (
    <>
      <Header />
      <div className="max-w-7xl mx-auto px-8 py-12">
        <h1 className="text-4xl font-light mb-12">Shop {folder}</h1>

        {loading ? (
          <p className="text-center text-gray-500">Loading products...</p>
        ) : products.length === 0 ? (
          <p className="text-center text-gray-500">No products found.</p>
        ) : (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-8">
            {products.map((product) => {
              const images = getImagesArray(product.hinhAnh);
              const currentIndex = imgIndexes[product._id] ?? 0;
              const currentImage = images.length > 0 ? images[currentIndex] : null;

              return (
                <div key={product._id} className="cursor-pointer group relative">
                  <div className="overflow-hidden rounded-md border border-gray-200 group-hover:shadow-lg transition-shadow duration-300 relative">
                    {currentImage ? (
                      <img
                        src={`/images/${folder}/${currentImage}`}
                        alt={product.tenSanPham}
                        className="w-full h-[300px] object-cover group-hover:scale-105 transition-transform duration-300"
                        onError={(e) => (e.currentTarget.src = '/images/default-image.jpg')}
                      />
                    ) : (
                      <img
                        src="/images/default-image.jpg"
                        alt="default"
                        className="w-full h-[300px] object-cover"
                      />
                    )}

                    {images.length > 1 && (
                      <>
                        <span
                          onClick={(e) => {
                            e.stopPropagation();
                            changeImageIndex(product._id, 'prev');
                          }}
                          className="absolute top-1/2 left-2 -translate-y-1/2 cursor-pointer select-none text-black text-xl bg-white/70 hover:bg-white px-2 py-1 rounded-full opacity-0 group-hover:opacity-100 transition-opacity"
                          role="button"
                          aria-label="Previous image"
                        >
                          ‹
                        </span>
                        <span
                          onClick={(e) => {
                            e.stopPropagation();
                            changeImageIndex(product._id, 'next');
                          }}
                          className="absolute top-1/2 right-2 -translate-y-1/2 cursor-pointer select-none text-black text-xl bg-white/70 hover:bg-white px-2 py-1 rounded-full opacity-0 group-hover:opacity-100 transition-opacity"
                          role="button"
                          aria-label="Next image"
                        >
                          ›
                        </span>
                      </>
                    )}
                  </div>
                  <h3 className="mt-3 text-sm font-semibold text-gray-900">
                    {product.tenSanPham}
                  </h3>
                  <p className="text-sm text-gray-700 mt-1">
                    {product.gia.toLocaleString('vi-VN')} VND
                  </p>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </>
  );
};

export default Products;
