import React, { useState } from 'react';
import axios from 'axios';

const ChatWidget = () => {
  const [messages, setMessages] = useState([
    { role: 'assistant', content: 'Xin chào! Tôi có thể giúp gì cho bạn hôm nay?' }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const response = await axios.post('https://api.openai.com/v1/chat/completions', {
        model: 'gpt-3.5-turbo',
        messages: newMessages,
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `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`, 
        }
      });

      const reply = response.data.choices[0].message.content;
      setMessages([...newMessages, { role: 'assistant', content: reply }]);
    } catch (error) {
      setMessages([...newMessages, { role: 'assistant', content: 'Lỗi khi gọi OpenAI API!' }]);
      console.error(error);
    }

    setLoading(false);
  };

  return (
    <div className="fixed bottom-6 right-6 w-80 h-[500px] bg-white shadow-xl rounded-lg border flex flex-col z-50">
      <div className="flex-1 overflow-auto p-3 space-y-2">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`p-2 rounded-md text-sm ${msg.role === 'user' ? 'bg-black text-white self-end' : 'bg-gray-200 text-black self-start'}`}
          >
            {msg.content}
          </div>
        ))}
      </div>
      <div className="p-3 border-t flex gap-2">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          className="flex-1 border px-2 py-1 text-sm"
          placeholder="Nhập tin nhắn..."
        />
        <button
          onClick={sendMessage}
          disabled={loading}
          className="bg-black text-white px-3 py-1 text-sm"
        >
          Gửi
        </button>
      </div>
    </div>
  );
};

export default ChatWidget;
