import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { updateQuantity, removeItem } from './redux/shopping_cart';
import Header from './Header';
const CartPage = () => {
  const cartItems = useSelector(state => state.cart.items);
  const dispatch = useDispatch();

  return (
   
    <div>
         <Header />
      {cartItems.length === 0 ? (
        <p>Giỏ hàng trống</p>
      ) : (
        cartItems.map(item => (
          <div key={item.id} style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>
            <img src={item.image} alt={item.name} style={{ width: 80, height: 80, objectFit: 'cover' }} />
            <p>{item.name}</p>
            <p>{item.price} VND</p>

            <input
              type="number"
              value={item.quantity}
              min="1"
              onChange={(e) =>
                dispatch(updateQuantity({ id: item.id, quantity: Number(e.target.value) }))
              }
            />

            <button onClick={() => dispatch(removeItem(item.id))}>Xoá</button>
          </div>
        ))
      )}
    </div>
  );
};

export default CartPage;
